CREATE FUNCTION actualizar_saldosnew(codigo_cta character varying, fecha_ini date, fecha_cie date, periodo character varying, debcre character varying, monto numeric) RETURNS integer
    AS $$
DECLARE
   REGISTRO RECORD;
   --SALDO_ANT NUMERIC (14,2);
   SALDO_ANT NUMERIC;
   --SALDO_ACT NUMERIC (14,2);
   SALDO_ACT NUMERIC;
   PERIODO_ANT VARCHAR;
   DEBITO_CREDITO VARCHAR;
   SALDOS CURSOR IS SELECT * FROM "SIMA002".CONTABB1 WHERE CODCTA = CODIGO_CTA
                                             AND FECINI = FECHA_INI
                                             AND FECCIE = FECHA_CIE
                                             ORDER BY PEREJE;
   --MONDEB NUMERIC(14,2);
   MONDEB NUMERIC;
   --MONCRE NUMERIC(14,2);
   MONCRE NUMERIC;
BEGIN
   SELECT  INTO SALDO_ANT SALANT
     FROM "SIMA002".CONTABB
    WHERE CODCTA = CODIGO_CTA AND
                 FECINI = FECHA_INI AND
                 FECCIE = FECHA_CIE;
   PERIODO_ANT := '00';
   OPEN SALDOS;
   FETCH SALDOS INTO REGISTRO;
   IF FOUND THEN
     LOOP
         IF REGISTRO.PEREJE=RTRIM(PERIODO) THEN
            IF RTRIM(DEBCRE)='D' THEN
               MONDEB:=MONTO;
               MONCRE:=0;
            ELSE
               MONDEB:=0;
               MONCRE:=MONTO;
            END IF;
         ELSE
            MONDEB:=0;
            MONCRE:=0;
         END IF;
         UPDATE "SIMA002".CONTABB1
            SET SALACT = ((REGISTRO.TOTDEB+MONDEB)  + SALDO_ANT) -
(REGISTRO.TOTCRE+MONCRE),
                TOTDEB = TOTDEB+MONDEB,
                TOTCRE = TOTCRE+MONCRE
          WHERE CODCTA = REGISTRO.CODCTA AND
                FECINI = REGISTRO.FECINI AND
                FECCIE = REGISTRO.FECCIE AND
                PEREJE = REGISTRO.PEREJE;
      PERIODO_ANT := LPAD(LTRIM(TO_CHAR(TO_NUMBER(PERIODO_ANT,99)+1,'99')),2,'0');
      SELECT INTO SALDO_ANT SALACT
        FROM "SIMA002".CONTABB1
       WHERE CODCTA = CODIGO_CTA AND
                    FECINI = FECHA_INI AND
                    FECCIE = FECHA_CIE AND
                    PEREJE = PERIODO_ANT;
        FETCH SALDOS INTO REGISTRO;
        IF NOT FOUND THEN
           EXIT;
        END IF;
     END LOOP;
   END IF;
   CLOSE SALDOS;
   RETURN 0;
END;
$$
    LANGUAGE plpgsql;


