CREATE FUNCTION obtener_mascara() RETURNS character varying
    AS $$
DECLARE
  TIRA VARCHAR(36);
  CONTADOR INTEGER;
  REG RECORD;
  MASCARA CURSOR IS SELECT NOMABR FROM CPNIVELES ORDER BY CONSEC;
BEGIN
  CONTADOR:=1;
  TIRA:='';

   OPEN MASCARA;
   FETCH MASCARA INTO REG;
   IF FOUND THEN
     LOOP
          IF CONTADOR = 1 THEN
              TIRA := TIRA || REG.NOMABR;
          ELSE
              TIRA := TIRA || '-' || REG.NOMABR;
          END IF;
          CONTADOR:=CONTADOR+1;
          FETCH MASCARA INTO REG;
          IF NOT FOUND THEN
             EXIT;
          END IF;
     END LOOP;
   END IF;
   CLOSE MASCARA;
   RETURN TIRA;
END;
$$
